<?class Dormio_Factory {  static $_cache = array();  function __construct(PDO $db) {    $this->db = $db;  }    function get($model, $pk=null) {    if(!class_exists($model)) throw new Dormio_Exception('No such model: ' . $model);    $obj = new $model($this->db);    if($pk) $obj->load($pk);    return $obj;  }    function manager($name) {    if(!isset(self::$_cache[$name])) self::$_cache[$name] = new Dormio_Manager($name, $this->db);    return self::$_cache[$name];  }}?>